import "./NewExpense.css"
import ExpenseForm from "./ExpenseForm";
import { useState } from "react";
const NewExpense = (props) => {

    const initial = <button onClick={openFormHandler}>Add New Expense</button>
    const [newExpenseContent,setNewExpenseContent] = useState(<button onClick={openFormHandler}>Add New Expense</button>);

    //changes
    const openFormHandler = (event) =>{
        setNewExpenseContent(

            <ExpenseForm onCancel = {closeFormHandler} onSaveExpenseData = {saveExpenseDataHandler}></ExpenseForm>


        );
    };
    //changes
    const closeFormHandler = () =>{
        setNewExpenseContent(initial);
    }

    const saveExpenseDataHandler = (saveExpenseData) =>{
        const expenseData = {
            ...saveExpenseData,
            id: Math.random().toString()
        };
        props.onAddExpense(expenseData);
        //changes below

    }
    return (
        <div className="new-expense">
            {newExpenseContent}

        </div>
    );
}
export default NewExpense;
